import axios from "axios";
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const initialState = {
  user: null,
  token: null, // Tambahkan token ke state
  isError: false,
  isSuccess: false,
  isLoading: false,
  message: ""
};

// Async thunk untuk login
export const LoginUser = createAsyncThunk(
  "auth/loginUser",
  async (user, thunkAPI) => {
    try {
      const response = await axios.post("http://localhost:5000/login", {
        email: user.email,
        password: user.password
      });

      // Simpan token di localStorage
      const { token } = response.data;
      if (token) {
        localStorage.setItem("token", token);
        axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
      }

      return response.data;
    } catch (error) {
      if (error.response) {
        const message = error.response.data.msg;
        return thunkAPI.rejectWithValue(message);
      }
    }
  }
);

// Slice Redux untuk auth
export const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {
    reset: (state) => initialState,
    logout: (state) => {
      state.user = null;
      state.token = null;
      localStorage.removeItem("token"); // Hapus token dari localStorage
      delete axios.defaults.headers.common["Authorization"]; // Hapus header Authorization
    }
  },
  extraReducers: (builder) => {
    builder.addCase(LoginUser.pending, (state) => {
      state.isLoading = true;
    });
    builder.addCase(LoginUser.fulfilled, (state, action) => {
      state.isLoading = false;
      state.isSuccess = true;
      state.user = action.payload.user;
      state.token = action.payload.token; // Simpan token di Redux state
    });
    builder.addCase(LoginUser.rejected, (state, action) => {
      state.isLoading = false;
      state.isError = true;
      state.message = action.payload;
    });
  }
});

export const { reset, logout } = authSlice.actions;
export default authSlice.reducer;

export const checkAuth = () => {
  const token = localStorage.getItem("token");
  return token || null; // Mengembalikan null jika token tidak ada
};

