import React, { lazy, useEffect } from "react";
import "./App.css";
import {
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import { themeChange } from "theme-change";
import checkAuth from "./app/auth";
import initializeApp from "./app/init";

// Importing pages
const Layout = lazy(() => import("./containers/Layout"));
const Login = lazy(() => import("./pages/Login"));
const ForgotPassword = lazy(() => import("./pages/ForgotPassword"));
const Register = lazy(() => import("./pages/Register"));
const Documentation = lazy(() => import("./pages/Documentation"));
const AllProducts = lazy(() => import("./features/product/allProduct"));
const DetailProduct = lazy(() =>
  import("./features/product/allProduct/detailProduct")
);
const HideLayout = lazy(() => import("./containers/HideLayout"));
const POS = lazy(() => import("./features/sales/pos"));

// Initializing different libraries
initializeApp();

// Check for login and initialize axios
const token = checkAuth();

function App() {
  useEffect(() => {
    // ðŸ‘† daisy UI themes initialization
    themeChange(false);
  }, []);

  return (
    <Routes>
      <Route path="/login" element={<Login />} />
      <Route path="/forgot-password" element={<ForgotPassword />} />
      <Route path="/register" element={<Register />} />
      <Route path="/documentation" element={<Documentation />} />

      {/* Routes for /app/* */}
      <Route path="/app/*" element={<Layout />}>
        {/* All Products Page */}
        <Route path="product-allProduct" element={<AllProducts />} />
        {/* Product Detail Page */}
        <Route path="product-allProduct/detailProduct/:id" element={<DetailProduct />} />
      </Route>

      {/* Catch-all route */}
      <Route
        path="*"
        element={<Navigate to={token ? "/app/dashboard" : "/login"} replace />}
      />
    </Routes>
  );
}

export default App;
