import moment from "moment"
import { useEffect, useState } from "react"
import axios from "axios";
import { jwtDecode } from "jwt-decode"
import {useNavigate} from "react-router-dom"
import { useDispatch, useSelector } from "react-redux"
import TitleCard from "../../../components/Cards/TitleCard"
import { showNotification } from '../../common/headerSlice'
import InputText from '../../../components/Input/InputText'
import TextAreaInput from '../../../components/Input/TextAreaInput'
import ToogleInput from '../../../components/Input/ToogleInput'

function ProfileSettings(){

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [role, setRole] = useState('');
    const [errorMessage, setErrorMessage] = useState("")
    const [password, setPassword] = useState('');

    const submitForm = async (e) => {
        e.preventDefault();
        setErrorMessage("");
    
        try {
            await axios.post('http://localhost:5000/login', {
                name: name,
                email: email,
                role: role,
                password: password

            });
        } catch (error) {
            if (error.response) {
                console.log("Error Response:", error.response.data);
                setErrorMessage(error.response.data.msg || "An error occurred");
            } else {
                setErrorMessage("Something went wrong. Please try again.");
            }
        }
    };

    const updateFormValue = ({ updateType, value }) => {
        setErrorMessage("");
        if (updateType === "name") setName(value);
        if (updateType === "email") setEmail(value);
        if (updateType === "password") setPassword(value);
        if (updateType === "role") setRole(value);
    };   


    const dispatch = useDispatch()

    // Call API to update profile settings changes
    const updateProfile = () => {
        dispatch(showNotification({message : "Profile Updated", status : 1}))    
    }

    return(
        <>
            
            <TitleCard title="Profile Settings" topMargin="mt-2">

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <InputText 
                        labelTitle="Name" 
                        defaultValue="Alex" 
                        updateFormValue={updateFormValue}
                    />
                    <InputText 
                        labelTitle="Email Id" 
                        defaultValue="alex@dashwind.com" 
                        updateFormValue={updateFormValue}
                    />
                    <InputText 
                        labelTitle="Role" 
                        defaultValue="Admin" 
                        updateFormValue={updateFormValue}
                    />                               
                    <InputText 
                        labelTitle="Password" 
                        defaultValue="*********" 
                        updateFormValue={updateFormValue}
                    />
                </div>
                <div className="divider" ></div>              

                <div className="mt-16"><button className="btn btn-primary float-right" onClick={() => updateProfile()}>Update</button></div>
            </TitleCard>
        </>
    )
}


export default ProfileSettings
