import moment from "moment";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import TitleCard from "../../../components/Cards/TitleCard";
import { showNotification } from '../../common/headerSlice';
import InputText from '../../../components/Input/InputText';

function ProfileSettings() {
    const dispatch = useDispatch();

    // State untuk menyimpan data formulir
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        role: "",
        password: "",
    });

    // Data pengguna dari Redux store (misalnya: authSlice)
    const { user } = useSelector((state) => state.auth);

    // Ambil data pengguna yang sedang login
    useEffect(() => {
        if (user) {
            setFormData({
                name: user.name || "",
                email: user.email || "",
                role: user.role || "User",
                password: "********", // Default untuk keamanan
            });
        }
    }, [user]);

    // Perbarui nilai input saat diubah
    const updateFormValue = (updateType, value) => {
        setFormData((prev) => ({
            ...prev,
            [updateType]: value,
        }));
    };

    // Kirim data yang diperbarui ke server
    const updateProfile = async () => {
        try {
            // Panggil API untuk memperbarui profil
            console.log("Updated Profile:", formData);
            dispatch(showNotification({ message: "Profile Updated", status: 1 }));
        } catch (error) {
            console.error("Failed to update profile:", error);
        }
    };

    return (
        <>
            <TitleCard title="Profile Settings" topMargin="mt-2">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <InputText
                        labelTitle="Name"
                        defaultValue={formData.name}
                        updateFormValue={(value) => updateFormValue("name", value)}
                    />
                    <InputText
                        labelTitle="Email Id"
                        defaultValue={formData.email}
                        updateFormValue={(value) => updateFormValue("email", value)}
                    />
                    <div className="form-control">
                        <label className="label"><span className="label-text">Role</span></label>
                        <select
                            className="select select-bordered"
                            value={formData.role}
                            onChange={(e) => updateFormValue("role", e.target.value)}
                        >
                            <option value="Admin">Admin</option>
                            <option value="User">User</option>
                            <option value="Guest">Guest</option>
                        </select>
                    </div>
                    <InputText
                        labelTitle="Password"
                        defaultValue={formData.password}
                        updateFormValue={(value) => updateFormValue("password", value)}
                    />
                </div>
                <div className="divider"></div>
                <div className="mt-16">
                    <button className="btn btn-primary float-right" onClick={updateProfile}>
                        Update
                    </button>
                </div>
            </TitleCard>
        </>
    );
}

export default ProfileSettings;
