import axios from "axios";

const checkAuth = async () => {
 
        try {
            const response = await axiosJWT.get('http://localhost:5000/token');
            setToken(response.data.accessToken);
            const decoded = jwtDecode(response.data.accessToken);
            console.log(decoded);
            setName(decoded.name);
            setEmail(decoded.email);
            setExpire(decoded.exp);
        } catch (error) {
            navigate('/login'); // Arahkan ke halaman login jika terjadi kesalahan
        }

    const PUBLIC_ROUTES = ["login", "forgot-password", "register", "documentation"];
    const isPublicPage = PUBLIC_ROUTES.some(r => window.location.href.includes(r));

    if (!accessToken && !isPublicPage) {
        window.location.href = '/login';
        return false;
    } else {
        axios.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`;

        // Cek validitas session dengan memanggil endpoint Me
        try {
            const response = await axios.get('/api/me'); // Sesuaikan endpoint dengan yang Anda miliki
            // Jika berhasil, berarti session masih valid
            return accessToken;
        } catch (error) {
            // Jika terjadi error (misalnya 401), redirect ke login
            if (error.response && error.response.status === 401) {
                window.location.href = '/login';
            }
            return false;
        }
        
    }
};

export default checkAuth;
