import { useEffect, useState, useNavigate } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode"
import ErrorText from "../../../../components/Typography/ErrorText";

function ViewModalBodyUser({ closeModal, id }) {
    const [userObj, setUserObj] = useState(null);
    const [users, setUser] =  useState([]);
    const [name, setName] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [role, setRole] = useState(''); 
    const [status, setStatus] = useState(''); 
    const [errorMessage, setErrorMessage] = useState("");
    const [token, setToken] = useState('');
    const [expire, setExpire] = useState('');
    const [loading, setLoading] = useState(true);

    // Fungsi untuk refresh token jika diperlukan
    const refreshToken = async () => {
        try {
            const response = await axiosJWT.get('http://localhost:5000/token');
            setToken(response.data.accessToken);
            const decoded = jwtDecode(response.data.accessToken);
            console.log(decoded);
            setName(decoded.name);
            setEmail(decoded.email);
            setExpire(decoded.exp);
        } catch (error) {
            navigate('/login'); // Arahkan ke halaman login jika terjadi kesalahan
        }
    };

    const axiosJWT = axios.create();

    // Tambahkan interceptor axios untuk mengatur token di setiap permintaan
    axiosJWT.interceptors.request.use(
        async (config) => {
            const currentDate = new Date();
            if (expire * 1000 < currentDate.getTime()) {
                const response = await axios.get('http://localhost:5000/token');
                config.headers.Authorization = `Bearer ${response.data.accessToken}`;
                setToken(response.data.accessToken);
                const decoded = jwtDecode(response.data.accessToken);
                setName(decoded.name);
                setEmail(decoded.email);
                setExpire(decoded.exp);
            }
            return config;
        },
        (error) => {
            return Promise.reject(error);
        }
    );

    useEffect(() => {
        const fetchUser = async () => {
            setLoading(true);
            try {
                const response = await axios.get('http://localhost:5000/users', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                const users = response.data; // Ambil data pengguna dari response
                const user = users.find((u) => u.id === id); // Cari user berdasarkan userId
                if (user) {
                    setUserObj(user);
                } else {
                    setErrorMessage("User not found.");
                }
            } catch (error) {
                setErrorMessage(error.response?.data?.message || "Failed to fetch users."); // Tangani error
            } finally {
                setLoading(false);
            }
        };

        fetchUser();
    }, [id, token]); // Tambahkan token sebagai dependensi

    if (loading) {
        return <p className="text-center text-gray-500">Loading...</p>;
    }

    if (errorMessage) {
        return <ErrorText styleClass="mt-16">{errorMessage}</ErrorText>;
    }

    if (!userObj) {
        return <p className="text-center text-gray-500">User not found.</p>;
    }

    return (
        <>
            <div className="mt-4">
                <div className="flex items-center rounded-lg p-3">
                    <label className="block text-sm font-bold w-1/4">Profile</label>
                    <div className="avatar">
                        <div className="mask mask-squircle w-12 h-12 mr-4">
                            <img src={userObj.profilImg} />
                        </div>
                    </div>
                </div>
            </div>
            <div className="mt-4">
                <div className="flex items-center bg-gray-200 rounded-lg p-3">
                    <label className="block text-sm font-bold w-1/4">Name</label>
                    <p className="w-3/4">{userObj.Name}</p>
                </div>
            </div>
            <div className="mt-4">
                <div className="flex items-center rounded-lg p-3">
                    <label className="block text-sm font-bold w-1/4">Phone</label>
                    <p className="w-3/4">{userObj.phone}</p>
                </div>
            </div>
            <div className="mt-4">
                <div className="flex items-center bg-gray-200 rounded-lg p-3">
                    <label className="block text-sm font-bold w-1/4">Email</label>
                    <p className="w-3/4">{userObj.email}</p>
                </div>
            </div>            
            <div className="mt-4">
                <div className="flex items-center rounded-lg p-3">
                    <label className="block text-sm font-bold w-1/4">Role</label>
                    <p className="w-3/4">{userObj.phone}</p>
                </div>
            </div>
            
            <div className="mt-4">
                <div className="flex items-center bg-gray-200 rounded-lg p-3">
                    <label className="block text-sm font-bold w-1/4">Password</label>
                    <p className="w-3/4">{userObj.password}</p>
                </div>
            </div>
            <div className="mt-4">
                <div className="flex items-center rounded-lg p-3">
                    <label className="block text-sm font-bold w-1/4">Status</label>
                    <p className="w-3/4">{userObj.status}</p>
                </div>
            </div>

            {errorMessage && <ErrorText styleClass="mt-16">{errorMessage}</ErrorText>}
            <div className="modal-action">                
            <button className="btn btn-primary px-6" onClick={() => closeModal()}>OK</button>
            </div>
        </>
    );
}

export default ViewModalBodyUser;
