import { useState } from "react";

function PageControl({ totalItems, rowsPerPage, onPageChange }) {
  const [currentPage, setCurrentPage] = useState(1); // Set nilai awal ke 1
  
  // Pastikan totalItems dan rowsPerPage bukan 0
  const totalPages = Math.max(Math.ceil(totalItems / rowsPerPage), 1);
  const startItem = (currentPage - 1) * rowsPerPage + 1;
  const endItem = Math.min(currentPage * rowsPerPage, totalItems);

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
      onPageChange(rowsPerPage, newPage);
    }
  };

  return (
    <div className="flex flex-wrap justify-between items-center mt-4 gap-4">
      <span className="text-sm">
        {totalItems === 0 ? "No items" : `${startItem} - ${endItem} of ${totalItems}`}
      </span>
      <div className="flex space-x-2">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
          className="btn btn-outline btn-sm"
        >
          {"<"}
        </button>
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages || totalItems === 0}
          className="btn btn-outline btn-sm"
        >
          {">"}
        </button>
      </div>
    </div>
  );
}

export default PageControl;
