import routes, { filterRoutesByRole } from '../routes/sidebar';
import { NavLink, useLocation } from 'react-router-dom';
import SidebarSubmenu from './SidebarSubmenu';
import XMarkIcon from '@heroicons/react/24/outline/XMarkIcon';
import { useDispatch, useSelector } from 'react-redux';

function LeftSidebar() {
  const location = useLocation();
  const dispatch = useDispatch();

  const user = useSelector((state) => state.auth.user); // Ambil user dari Redux

  // Cek apakah user ada sebelum memfilter routes
  const filteredRoutes = user ? filterRoutesByRole(routes, user.role) : [];

  const close = (e) => {
    document.getElementById('left-sidebar-drawer').click();
  };

  return (
    <div className="drawer-side z-30">
      <label htmlFor="left-sidebar-drawer" className="drawer-overlay"></label>
      <ul className="menu pt-2 w-80 bg-base-100 min-h-full text-base-content">
        <button
          className="btn btn-ghost bg-base-300 btn-circle z-50 top-0 right-0 mt-4 mr-2 absolute lg:hidden"
          onClick={() => close()}
        >
          <XMarkIcon className="h-5 inline-block w-5" />
        </button>

        <li className="mb-2 text-xl font-semibold">
          <Link to={'/app/welcome'} className="flex items-center gap-2">
            <img className="mask mask-squircle w-9" src="/logo.svg" alt="DashWind Logo" />
            <h1 className="text-2xl mt-0 font-bold text-center">
              <span className="text-primary">Dash</span>
              <span className="text-accentcont">Stack</span>
            </h1>
          </Link>
        </li>

        {filteredRoutes.map((route, k) => (
          <li className="" key={k}>
            {route.submenu ? (
              <SidebarSubmenu {...route} />
            ) : (
              <NavLink
                end
                to={route.path}
                className={({ isActive }) => `${isActive ? 'font-semibold bg-secondary' : 'font-normal'}`}
              >
                {route.icon} {route.name}
                {location.pathname === route.path ? (
                  <span className="absolute inset-y-0 left-0 w-1 rounded-tr-md rounded-br-md bg-primary" aria-hidden="true"></span>
                ) : null}
              </NavLink>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default LeftSidebar;
